package samltools

import (
	"encoding/base64"
	"log"
	"os"
)

func readX509Certificate() {
	entry := ``
	derBytesCert, err := base64.StdEncoding.DecodeString(entry)
	if err != nil {
		log.Fatalf("Error in parsing %s", err.Error())
	}
	certFile, _ := os.Create("x5c.der")
	certFile.Write(derBytesCert)

}
